import asyncio
from roblox import Client  # Install with: pip install robloxapi

# Hardcoded .ROBLOSECURITY token (highly insecure, for educational purposes only)
TOKEN = "_|WARNING:-DO-NOT-SHARE-THIS.--Sharing-this-will-allow-someone-to-log-in-as-you-and-to-steal-your-ROBUX-and-items.|_CAEaAhAB."

# Create the client with the token for authentication
client = Client(TOKEN)

async def main():
    try:
        # Get the authenticated user (yourself) to verify login and user ID
        me = await client.user
        print(f'Logged in as {me.name} (ID: {me.id})')
        
        # Replace with your user ID (obtained from profile URL, e.g., https://www.roblox.com/users/123456789/profile)
        TARGET_USER_ID = 9280630447  # Replace with your actual user ID
        
        # Fetch the target user
        target_user = await client.get_user_by_id(TARGET_USER_ID)
        if target_user is None:
            print(f'Error: User with ID {TARGET_USER_ID} not found.')
            return
        
        # Follow the user with a delay to respect rate limits
        await asyncio.sleep(2)
        await target_user.follow()
        print(f'Successfully followed {target_user.name}')
        
        # Send a friend request with a delay to respect rate limits
        await asyncio.sleep(2)
        await target_user.request_friend()
        print(f'Sent friend request to {target_user.name}')
        
    except Exception as e:
        print(f'Error: {e}')

# Run the async main function
if __name__ == "__main__":
    asyncio.run(main())
